import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  // State variables for currencies, conversion inputs, and results
  const [currencies, setCurrencies] = useState([]);
  const [amount, setAmount] = useState(1); // Default amount to convert
  const [fromCurrency, setFromCurrency] = useState('USD'); // Default "from" currency
  const [toCurrency, setToCurrency] = useState('EUR'); // Default "to" currency
  const [convertedAmount, setConvertedAmount] = useState(null); // Conversion result
  const [error, setError] = useState(null); // Error handling
  const [loading, setLoading] = useState(false); // Loading state

  // Fetch exchange rates and available currencies
  useEffect(() => {
    const fetchExchangeRates = async () => {
      setLoading(true);
      setError(null);
      try {
        const response = await axios.get(
          `https://v6.exchangerate-api.com/v6/45b21c4620c8ead432a86234/latest/${fromCurrency}`
        );
        const rates = response.data.conversion_rates;
        setCurrencies(Object.keys(rates)); // Get list of all available currencies
        setConvertedAmount(amount * rates[toCurrency]); // Perform conversion
      } catch (err) {
        setError('Failed to fetch exchange rates. Please try again.');
      } finally {
        setLoading(false);
      }
    };

    fetchExchangeRates();
  }, [fromCurrency, toCurrency, amount]); // Re-run if currency or amount changes

  return (
    <div className="h-screen flex items-center justify-center bg-gray-100">
      {/* Main container */}
      <div className="max-w-md w-full p-6 bg-white rounded-lg shadow-md text-center">
        <h1 className="text-2xl font-bold text-gray-800 mb-6">Currency Converter</h1>

        {/* Amount Input */}
        <div className="mb-4">
          <label className="block text-left text-gray-600 text-sm mb-2">Amount</label>
          <input
            type="number"
            value={amount}
            onChange={(e) => setAmount(Math.max(0, parseFloat(e.target.value) || 0))}
            className="w-full px-4 py-2 border rounded-md"
          />
        </div>

        {/* From Currency Selector */}
        <div className="mb-4">
          <label className="block text-left text-gray-600 text-sm mb-2">From Currency</label>
          <select
            value={fromCurrency}
            onChange={(e) => setFromCurrency(e.target.value)}
            className="w-full px-4 py-2 border rounded-md"
          >
            {currencies.map((currency) => (
              <option key={currency} value={currency}>
                {currency}
              </option>
            ))}
          </select>
        </div>

        {/* To Currency Selector */}
        <div className="mb-4">
          <label className="block text-left text-gray-600 text-sm mb-2">To Currency</label>
          <select
            value={toCurrency}
            onChange={(e) => setToCurrency(e.target.value)}
            className="w-full px-4 py-2 border rounded-md"
          >
            {currencies.map((currency) => (
              <option key={currency} value={currency}>
                {currency}
              </option>
            ))}
          </select>
        </div>

        {/* Result or Loading/Error */}
        {loading ? (
          <p className="text-gray-500">Loading...</p>
        ) : error ? (
          <p className="text-red-500">{error}</p>
        ) : (
          <div className="mt-4">
            {convertedAmount && (
              <p className="text-xl font-semibold text-gray-800">
                {amount} {fromCurrency} = {convertedAmount.toFixed(2)} {toCurrency}
              </p>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
