// src/App.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [currencies, setCurrencies] = useState([]);  // Store available currencies
  const [amount, setAmount] = useState(1);  // Amount to be converted
  const [fromCurrency, setFromCurrency] = useState('USD');  // From currency
  const [toCurrency, setToCurrency] = useState('EUR');  // To currency
  const [convertedAmount, setConvertedAmount] = useState(null);  // Converted amount
  const [error, setError] = useState(null);  // Error state for API issues

  // Fetch currencies and exchange rates from the provided API
  useEffect(() => {
    const fetchExchangeRates = async () => {
      try {
        const response = await axios.get(
          'https://v6.exchangerate-api.com/v6/45b21c4620c8ead432a86234/latest/USD'
        );
        // Extract the conversion rates and currencies
        const conversionRates = response.data.conversion_rates;
        setCurrencies(Object.keys(conversionRates));  // Set available currencies
        convertCurrency(conversionRates);  // Convert currency using fetched rates
      } catch (err) {
        setError('Error fetching data.');
      }
    };

    fetchExchangeRates();
  }, [fromCurrency, toCurrency]);  // Re-fetch data when currency changes

  // Convert currency based on the exchange rates
  const convertCurrency = (conversionRates) => {
    if (conversionRates) {
      const rate = conversionRates[toCurrency];
      setConvertedAmount(amount * rate);
    }
  };

  return (
    <div className="App">
      <h1>Currency Converter</h1>

      <div>
        <label htmlFor="amount">Amount</label>
        <input
          type="number"
          id="amount"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
        />
      </div>

      <div>
        <label htmlFor="from-currency">From Currency</label>
        <select
          id="from-currency"
          value={fromCurrency}
          onChange={(e) => setFromCurrency(e.target.value)}
        >
          <option value="USD">USD</option> {/* You can hardcode USD as it's the base */}
          {/* Optionally add more currencies if needed */}
        </select>
      </div>

      <div>
        <label htmlFor="to-currency">To Currency</label>
        <select
          id="to-currency"
          value={toCurrency}
          onChange={(e) => setToCurrency(e.target.value)}
        >
          {currencies.map((currency) => (
            <option key={currency} value={currency}>
              {currency}
            </option>
          ))}
        </select>
      </div>

      <button onClick={() => convertCurrency()}>Convert</button>

      {error && <p>{error}</p>}

      {convertedAmount !== null && (
        <div>
          <h2>
            {amount} {fromCurrency} is equal to {convertedAmount.toFixed(2)} {toCurrency}
          </h2>
        </div>
      )}
    </div>
  );
};

export default App;
