// src/App.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [currencies, setCurrencies] = useState([]);  // Store available currencies
  const [amount, setAmount] = useState(1);  // Amount to be converted
  const [fromCurrency, setFromCurrency] = useState('USD');  // From currency
  const [toCurrency, setToCurrency] = useState('EUR');  // To currency
  const [convertedAmount, setConvertedAmount] = useState(null);  // Converted amount
  const [error, setError] = useState(null);  // Error state for API issues

  // Fetch currencies and exchange rates from an API
  useEffect(() => {
    // Replace 'YOUR_API_KEY' with your actual API key
    const fetchExchangeRates = async () => {
      try {
        const response = await axios.get(
          `https://v6.exchangerate-api.com/v6/YOUR_API_KEY/latest/${fromCurrency}`
        );
        setCurrencies(Object.keys(response.data.conversion_rates));  // Set available currencies
        convertCurrency();  // Convert the amount once the rates are fetched
      } catch (err) {
        setError("Error fetching data.");
      }
    };

    fetchExchangeRates();
  }, [fromCurrency, toCurrency]);  // Re-fetch data when currency changes

  // Convert currency whenever inputs change
  const convertCurrency = async () => {
    try {
      const response = await axios.get(
        `https://v6.exchangerate-api.com/v6/YOUR_API_KEY/latest/${fromCurrency}`
      );
      const conversionRate = response.data.conversion_rates[toCurrency];
      setConvertedAmount(amount * conversionRate);
    } catch (err) {
      setError("Error converting currency.");
    }
  };

  return (
    <div className="App">
      <h1>Currency Converter</h1>

      <div>
        <label htmlFor="amount">Amount</label>
        <input
          type="number"
          id="amount"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
        />
      </div>

      <div>
        <label htmlFor="from-currency">From Currency</label>
        <select
          id="from-currency"
          value={fromCurrency}
          onChange={(e) => setFromCurrency(e.target.value)}
        >
          {currencies.map((currency) => (
            <option key={currency} value={currency}>
              {currency}
            </option>
          ))}
        </select>
      </div>

      <div>
        <label htmlFor="to-currency">To Currency</label>
        <select
          id="to-currency"
          value={toCurrency}
          onChange={(e) => setToCurrency(e.target.value)}
        >
          {currencies.map((currency) => (
            <option key={currency} value={currency}>
              {currency}
            </option>
          ))}
        </select>
      </div>

      <button onClick={convertCurrency}>Convert</button>

      {error && <p>{error}</p>}

      {convertedAmount !== null && (
        <div>
          <h2>
            {amount} {fromCurrency} is equal to {convertedAmount.toFixed(2)} {toCurrency}
          </h2>
        </div>
      )}
    </div>
  );
};

export default App;
