import React, { useState, useEffect } from 'react';
import axios from 'axios';
import InputBox from './InputBox'; // Assuming InputBox is in the same folder

const App = () => {
  const [currencies, setCurrencies] = useState([]);
  const [amount, setAmount] = useState(1);
  const [fromCurrency, setFromCurrency] = useState('USD');
  const [toCurrency, setToCurrency] = useState('EUR');
  const [convertedAmount, setConvertedAmount] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchExchangeRates = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get(
        `https://v6.exchangerate-api.com/v6/45b21c4620c8ead432a86234/latest/${fromCurrency}`
      );
      const conversionRates = response.data.conversion_rates;
      setCurrencies(Object.keys(conversionRates));
      setConvertedAmount(amount * conversionRates[toCurrency]);
    } catch (err) {
      setError('Error fetching data.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchExchangeRates();
  }, [fromCurrency, toCurrency]);

  return (
    <div className="App max-w-md mx-auto p-5 bg-gray-100 rounded-lg shadow-md">
      <h1 className="text-2xl font-bold mb-5">Currency Converter</h1>

      <InputBox
        label="From"
        amount={amount}
        onAmountChange={setAmount}
        onCurrencyChange={setFromCurrency}
        currencyOptions={currencies}
        selectCurrency={fromCurrency}
      />

      <InputBox
        label="To"
        amount={convertedAmount ? convertedAmount.toFixed(2) : ''}
        amountDisable={true} // Disable input for "To" amount
        onCurrencyChange={setToCurrency}
        currencyOptions={currencies}
        selectCurrency={toCurrency}
      />

      <button
        onClick={fetchExchangeRates}
        className="mt-5 px-5 py-2 bg-blue-500 text-white font-semibold rounded-lg hover:bg-blue-600"
        disabled={!currencies.length || loading}
      >
        {loading ? 'Converting...' : 'Convert'}
      </button>

      {error && <p className="text-red-500 mt-3">{error}</p>}

      {!loading && convertedAmount !== null && !error && (
        <div className="mt-5 text-lg">
          <p>
            {amount} {fromCurrency} is equal to{' '}
            <strong>
              {convertedAmount.toFixed(2)} {toCurrency}
            </strong>
          </p>
        </div>
      )}
    </div>
  );
};

export default App;
