import React, { useState, useEffect } from "react";
import axios from "axios";
import Loader from "./components/Loader";

const App = () => {
  const [currencies, setCurrencies] = useState([]);
  const [amount, setAmount] = useState(1);
  const [fromCurrency, setFromCurrency] = useState("USD");
  const [toCurrency, setToCurrency] = useState("EUR");
  const [convertedAmount, setConvertedAmount] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchExchangeRates = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get(
        `https://v6.exchangerate-api.com/v6/45b21c4620c8ead432a86234/latest/${fromCurrency}`
      );
      const conversionRates = response.data.conversion_rates;
      setCurrencies(Object.keys(conversionRates));
      setConvertedAmount(amount * conversionRates[toCurrency]);
    } catch (err) {
      setError("Error fetching data.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchExchangeRates();
  }, [fromCurrency, toCurrency]);

  return (
    <div className="App h-screen flex items-center justify-center bg-gray-100">
      <div className="max-w-md w-full p-6 bg-white rounded-lg shadow-md text-center">
        <h1 className="text-2xl font-bold mb-6 text-gray-800">
          Currency Converter
        </h1>

        {/* Amount Input */}
        <div className="mb-4">
          <label
            htmlFor="amount"
            className="block text-left text-sm font-medium text-gray-700"
          >
            Amount
          </label>
          <input
            type="number"
            id="amount"
            value={amount}
            onChange={(e) =>
              setAmount(Math.max(0, parseFloat(e.target.value) || 0))
            }
            className="w-full px-4 py-2 border rounded-md shadow-sm focus:ring-2 focus:ring-green-500 focus:outline-none"
          />
        </div>

        {/* From Currency */}
        <div className="mb-4">
          <label
            htmlFor="from-currency"
            className="block text-left text-sm font-medium text-gray-700"
          >
            From Currency
          </label>
          <select
            id="from-currency"
            value={fromCurrency}
            onChange={(e) => setFromCurrency(e.target.value)}
            className="w-full px-4 py-2 border rounded-md shadow-sm focus:ring-2 focus:ring-green-500 focus:outline-none"
          >
            {currencies.map((currency) => (
              <option key={currency} value={currency}>
                {currency}
              </option>
            ))}
          </select>
        </div>

        {/* To Currency */}
        <div className="mb-4">
          <label
            htmlFor="to-currency"
            className="block text-left text-sm font-medium text-gray-700"
          >
            To Currency
          </label>
          <select
            id="to-currency"
            value={toCurrency}
            onChange={(e) => setToCurrency(e.target.value)}
            className="w-full px-4 py-2 border rounded-md shadow-sm focus:ring-2 focus:ring-green-500 focus:outline-none"
          >
            {currencies.map((currency) => (
              <option key={currency} value={currency}>
                {currency}
              </option>
            ))}
          </select>
        </div>

        {/* Convert Button */}
        <button
          onClick={fetchExchangeRates}
          className="w-full bg-green-500 text-white py-2 px-4 rounded-md shadow-md hover:bg-green-600 focus:ring-2 focus:ring-green-500 focus:outline-none"
          disabled={!currencies.length}
        >
          Convert
        </button>

        {/* Loading State */}
        {loading && <Loader />}

        {/* Error State */}
        {error && (
          <p className="mt-4 text-sm text-red-500">
            {error}. Please try again later.
          </p>
        )}

        {/* Conversion Result */}
        {convertedAmount !== null && !loading && !error && (
          <div className="mt-6">
            <h2 className="text-xl font-semibold text-gray-800">
              {amount} {fromCurrency} is equal to{" "}
              <span className="text-green-500">
                {convertedAmount.toFixed(2)}
              </span>{" "}
              {toCurrency}
            </h2>
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
